return-response.set-body:
@{
                var headers = context.Request.Headers
                                .Where(h => h.Key != "A" && h.Key != "B" && h.Key != "C")
                                .Select(h => string.Format("{0}: {1}", h.Key, String.Join(", ", h.Value)))
                                .ToArray(); 
                return String.Join(" ||| ", headers);
            }
